// Inline Enhanced Simulation (—Ä–æ–∑—É–º–Ω–∞ –≤–µ—Ä—Å—ñ—è –∑ —Ä–∞–Ω–¥–æ–º–Ω–∏–º–∏ –ø–∞—Ä–∞–º–µ—Ç—Ä–∞–º–∏)
class SmartEnhancedSimulation {
    constructor(initialDeviceConfigs, calculateWQI, formatOperatingTime) {
        this.devices = JSON.parse(JSON.stringify(initialDeviceConfigs));
        this.calculateWQI = calculateWQI;
        this.formatOperatingTime = formatOperatingTime;
        this.simulationStartTime = Date.now();

        // –ì–µ–Ω–µ—Ä—É—î–º–æ —Ä–∞–Ω–¥–æ–º–Ω—ñ —Å—Ç–∞—Ä—Ç–æ–≤—ñ –ø–∞—Ä–∞–º–µ—Ç—Ä–∏ –¥–ª—è –∫–æ–∂–Ω–æ–≥–æ –ø—Ä–∏—Å—Ç—Ä–æ—é
        this.initializeRandomBaselines();

        node.log('üåä Smart Enhanced Simulation —ñ–Ω—ñ—Ü—ñ–∞–ª—ñ–∑–æ–≤–∞–Ω–æ –∑ —Ä–∞–Ω–¥–æ–º–Ω–∏–º–∏ –ø–∞—Ä–∞–º–µ—Ç—Ä–∞–º–∏');
    }

    initializeRandomBaselines() {
        for (const deviceId in this.devices) {
            const device = this.devices[deviceId];

            // –ì–µ–Ω–µ—Ä—É—î–º–æ —Ä–∞–Ω–¥–æ–º–Ω—ñ —Å—Ç–∞—Ä—Ç–æ–≤—ñ –ø–∞—Ä–∞–º–µ—Ç—Ä–∏ –≤ —Ä–µ–∞–ª—ñ—Å—Ç–∏—á–Ω–∏—Ö –¥—ñ–∞–ø–∞–∑–æ–Ω–∞—Ö
            device.baseline = {
                ph: this.randomInRange(5.5, 9.5),        // pH –≤—ñ–¥ –∫–∏—Å–ª–æ–≥–æ –¥–æ –ª—É–∂–Ω–æ–≥–æ
                temp: this.randomInRange(8, 35),          // –¢–µ–º–ø–µ—Ä–∞—Ç—É—Ä–∞ –≤—ñ–¥ —Ö–æ–ª–æ–¥–Ω–æ—ó –¥–æ —Ç–µ–ø–ª–æ—ó
                tds: this.randomInRange(50, 1500),        // TDS –≤—ñ–¥ —á–∏—Å—Ç–æ—ó –¥–æ —Å–æ–ª–æ–Ω–æ—ó –≤–æ–¥–∏
                turbidity: this.randomInRange(0.1, 5.0)    // –ö–∞–ª–∞–º—É—Ç–Ω—ñ—Å—Ç—å –≤—ñ–¥ –ø—Ä–æ–∑–æ—Ä–æ—ó –¥–æ –º—É—Ç–Ω–æ—ó
            };

            // –í—Å—Ç–∞–Ω–æ–≤–ª—é—î–º–æ –ø–æ—Ç–æ—á–Ω—ñ –∑–Ω–∞—á–µ–Ω–Ω—è —Ä—ñ–≤–Ω–∏–º–∏ baseline
            device.current = {
                ...device.baseline,
                wqi: 0,
                lastUpdate: Date.now()
            };

            // –ì–µ–Ω–µ—Ä—É—î–º–æ —Ä–∞–Ω–¥–æ–º–Ω—ñ —Ç—Ä–µ–Ω–¥–∏ –¥–ª—è –∫–æ–∂–Ω–æ–≥–æ –ø–∞—Ä–∞–º–µ—Ç—Ä–∞
            device.trends = {
                ph: this.randomInRange(-0.02, 0.02),        // –¢—Ä–µ–Ω–¥ pH
                temp: this.randomInRange(-0.05, 0.05),      // –¢—Ä–µ–Ω–¥ —Ç–µ–º–ø–µ—Ä–∞—Ç—É—Ä–∏
                tds: this.randomInRange(-2, 2),             // –¢—Ä–µ–Ω–¥ TDS
                turbidity: this.randomInRange(-0.03, 0.03)  // –¢—Ä–µ–Ω–¥ –∫–∞–ª–∞–º—É—Ç–Ω–æ—Å—Ç—ñ
            };

            node.log(`üé≤ –ü—Ä–∏—Å—Ç—Ä—ñ–π ${deviceId} - pH:${device.baseline.ph.toFixed(2)}, T:${device.baseline.temp.toFixed(1)}¬∞C, TDS:${Math.round(device.baseline.tds)}, Turb:${device.baseline.turbidity.toFixed(1)}`);
        }
    }

    randomInRange(min, max) {
        return Math.random() * (max - min) + min;
    }

    generateDeviceData(device, timestamp) {
        const baseline = device.baseline;
        const trends = device.trends;
        const timeMs = timestamp - this.simulationStartTime;
        const timeMins = timeMs / (1000 * 60);

        // –°–∏–Ω—É—Å–æ—ó–¥–∞–ª—å–Ω—ñ –∫–æ–ª–∏–≤–∞–Ω–Ω—è –∑ —Ä—ñ–∑–Ω–∏–º–∏ –ø–µ—Ä—ñ–æ–¥–∞–º–∏
        const phCycle = Math.sin(timeMins * 0.05) * 0.8;     // –ü–æ–≤—ñ–ª—å–Ω–∏–π —Ü–∏–∫–ª pH
        const tempCycle = Math.cos(timeMins * 0.03) * 3;     // –¢–µ–º–ø–µ—Ä–∞—Ç—É—Ä–Ω—ñ –∫–æ–ª–∏–≤–∞–Ω–Ω—è
        const tdsCycle = Math.sin(timeMins * 0.07) * 100;    // –ö–æ–ª–∏–≤–∞–Ω–Ω—è TDS
        const turbidityCycle = Math.cos(timeMins * 0.09) * 2; // –ö–æ–ª–∏–≤–∞–Ω–Ω—è –∫–∞–ª–∞–º—É—Ç–Ω–æ—Å—Ç—ñ

        // –î–æ–≤–≥–æ—Å—Ç—Ä–æ–∫–æ–≤—ñ —Ç—Ä–µ–Ω–¥–∏
        const trendPH = trends.ph * timeMins * 0.01;
        const trendTemp = trends.temp * timeMins * 0.01;
        const trendTDS = trends.tds * timeMins * 0.01;
        const trendTurbidity = trends.turbidity * timeMins * 0.01;

        // –í–∏–ø–∞–¥–∫–æ–≤–∏–π —à—É–º
        const noise = () => (Math.random() - 0.5) * 0.3;

        // –†–æ–∑—Ä–∞—Ö—É–Ω–æ–∫ –∑–Ω–∞—á–µ–Ω—å –∑ —É—Ä–∞—Ö—É–≤–∞–Ω–Ω—è–º —Ü–∏–∫–ª—ñ–≤, —Ç—Ä–µ–Ω–¥—ñ–≤ —Ç–∞ —à—É–º—É
        const ph = Math.max(0, Math.min(14, baseline.ph + phCycle + trendPH + noise()));
        const temp = Math.max(0, Math.min(50, baseline.temp + tempCycle + trendTemp + noise() * 2));
        const tds = Math.max(0, Math.min(3000, baseline.tds + tdsCycle + trendTDS + noise() * 20));
        const turbidity = Math.max(0, Math.min(100, baseline.turbidity + turbidityCycle + trendTurbidity + noise()));

        // –Ü–Ω–æ–¥—ñ –≥–µ–Ω–µ—Ä—É—î–º–æ "–ø–æ–¥—ñ—ó" - —Ä—ñ–∑–∫—ñ –∑–º—ñ–Ω–∏ –ø–∞—Ä–∞–º–µ—Ç—Ä—ñ–≤
        const eventChance = Math.random();
        let eventMultiplier = { ph: 1, temp: 1, tds: 1, turbidity: 1 };

        if (eventChance < 0.05) { // 5% —à–∞–Ω—Å –ø–æ–¥—ñ—ó
            const eventType = Math.random();
            if (eventType < 0.25) {
                // –ö–∏—Å–ª–æ—Ç–Ω–∏–π –≤–∏–∫–∏–¥
                eventMultiplier.ph = 0.7;
                device.currentEvent = "–ö–∏—Å–ª–æ—Ç–Ω–∏–π –≤–∏–∫–∏–¥";
            } else if (eventType < 0.5) {
                // –¢–µ–º–ø–µ—Ä–∞—Ç—É—Ä–Ω–∏–π —Å—Ç—Ä–∏–±–æ–∫
                eventMultiplier.temp = 1.3;
                device.currentEvent = "–¢–µ–º–ø–µ—Ä–∞—Ç—É—Ä–Ω–∏–π —Å—Ç—Ä–∏–±–æ–∫";
            } else if (eventType < 0.75) {
                // –ó–∞–±—Ä—É–¥–Ω–µ–Ω–Ω—è (TDS)
                eventMultiplier.tds = 1.5;
                device.currentEvent = "–ü—ñ–¥–≤–∏—â–µ–Ω–Ω—è TDS";
            } else {
                // –ü—ñ–¥–≤–∏—â–µ–Ω–∞ –∫–∞–ª–∞–º—É—Ç–Ω—ñ—Å—Ç—å
                eventMultiplier.turbidity = 2;
                device.currentEvent = "–ü—ñ–¥–≤–∏—â–µ–Ω–∞ –∫–∞–ª–∞–º—É—Ç–Ω—ñ—Å—Ç—å";
            }
        } else if (eventChance > 0.95) {
            // –û—á–∏—â–µ–Ω–Ω—è –ø–æ–¥—ñ—ó
            device.currentEvent = null;
        }

        return {
            ph: Math.round(ph * eventMultiplier.ph * 100) / 100,              // 2 –∑–Ω–∞–∫–∏ –ø—ñ—Å–ª—è –∫–æ–º–∏
            temp: Math.round(temp * eventMultiplier.temp * 10) / 10,          // 1 –∑–Ω–∞–∫ –ø—ñ—Å–ª—è –∫–æ–º–∏  
            tds: Math.round(tds * eventMultiplier.tds),                       // —Ü—ñ–ª—ñ —á–∏—Å–ª–∞
            turbidity: Math.round(turbidity * eventMultiplier.turbidity * 10) / 10   // 1 –∑–Ω–∞–∫ –ø—ñ—Å–ª—è –∫–æ–º–∏
        };
    }
}

