    node.warn("Enhanced simulation not available, using basic simulation");
    // Fallback –¥–æ —Å—Ç–∞—Ä–æ—ó —Å–∏–º—É–ª—è—Ü—ñ—ó
    for (const deviceId in devices) {
        if (devices.hasOwnProperty(deviceId)) {
            let device = devices[deviceId];
            let baseline = device.baseline;
            let current = device.current;
            let technical = device.technical;

            // –°—Ç–∞—Ä–∞ —Å–∏–º—É–ª—è—Ü—ñ—è –Ω–æ—Ä–º–∞–ª—å–Ω–∏—Ö —Ñ–ª—É–∫—Ç—É–∞—Ü—ñ–π
