            current.turbidity = parseFloat(Math.max(0, Math.min(50, current.turbidity)).toFixed(1));

            // –û–Ω–æ–≤–ª–µ–Ω–Ω—è WQI
            const scores = calculateWQI(current);
            current.wqi = scores.totalWQI;
            current.partial_scores = scores.partial;
            current.lastUpdate = Date.now();

            // –û–Ω–æ–≤–ª–µ–Ω–Ω—è —á–∞—Å—É —Ä–æ–±–æ—Ç–∏
            if (formatOperatingTime && technical.startTime) {
                technical.operatingTime = formatOperatingTime(technical.startTime);
